{let n=function(){let n=$("#new-post-form");n.submit((function(o){o.preventDefault(),$.ajax({type:"post",url:"/posts/create",data:n.serialize(),success:function(n){let o=e(n.data.post);$("#posts-list-container>ul").prepend(o),t($(" .delete-post-button",o))},error:function(n){console.log(n.responseText)}})}))},e=function(n){return $(`<li id="post-${n._id}">\n            <p>\n        \n            \x3c!-- Delete button for posts --\x3e\n            <small> \n                <a class="delete-post-button" href="/posts/destroy/${n._id}">X</a> \n            </small>\n \n            ${n.content}\n            <br>\n            <small>\n            ${n.user.name}\n            </small>\n            </p>\n        \x3c!-- Comments --\x3e\n        <div class="post-comments">\n\n                <form action="/comments/create" method="POST">\n                    \n                    <input type="text" name="content" placeholder="Add Comment" required>\n                    \x3c!-- Next we need to send the ID of the post to which the comment must be added. --\x3e\n                    <input type="hidden" name="post" value="${n._id}">\n                    <input type="submit" value="Add Comment">\n\n                </form>\n                \n\n            <div class="post-comments-list">\n                <ul id="post-comments-${n._id}">\n\n                   \n\n                </ul>\n\n\n            </div>\n\n        </div>\n\n    </li>  `)},t=function(n){$(n).click((function(n){n.preventDefault(),$.ajax({type:"get",url:$("delete-link").prop("href"),success:function(n){$("#post-"+n.data.post._id).removed()},error:function(n){console.log(n.responseText)}})}))};n()}